import React from "react";

class App extends React.Component {

	// Constructor
	constructor(props) {
		super(props);

		this.state = {
			items: [],
			DataisLoaded: false
		};
	}

	// ComponentDidMount is used to
	// execute the code
	componentDidMount() {
		fetch("https://api.spacexdata.com/v2/launches")
			.then((res) => res.json())
			.then((json) => {
				this.setState({
					items: json,
					DataisLoaded: true
				});
			})
	}
	render() {
		const { DataisLoaded, items } = this.state;
		if (!DataisLoaded) return <div>
			<h1> Pleses wait some time.... </h1> </div> ;

		return (
		<div className = "App">
			<h1> Fetch data from an api in react </h1> {
				items.map((item) => (

				<div key = { item.id } >

					User_Name: { item.flight_number },
					Full_Name: { item.launch_date_utc },
					
                <li className='user inline-flex m-[15px]' key={item.flight_number}>
                  <div className='rounded-md h-[200px] w-[300px] flex flex-col space-y-12 hover:bg-blue-600 transition duration-500 hover:scale-105 bg-[url(./asset/image/1.jpg)]'
                  style={{  
                    backgroundImage: "url(" + item.background + ")",
                    backgroundPosition: 'center',
                    backgroundSize: 'cover',
                    backgroundRepeat: 'no-repeat'
                  }}
                  >

                      
                      <div className=" text-3xl bg-yellow-200 rounded-t-md">{item.mission_name}</div>
                      <div className="bg-yellow-200 w-[150px] rounded-3xl ml-[75px]">
                        {item.rocket.rocket_name}
                      </div>

                      <div className="flex space-x-20 content-center ml-[70px]">

                          <button id={key} className="text-xl">
                            <LikeBtn />
                          </button>

                        <div className="text-3xl bg-yellow-200 rounded-xl">{val.price}</div>
                      </div>       
                  </div>
                </li>



				</div>











			))
			}
		</div>
	);
}
}

export default App;
