import React from "react";

class App extends React.Component {

	// Constructor
	constructor(props) {
		super(props);

		this.state = {
			items: [],
			DataisLoaded: false
		};
	}

	// ComponentDidMount is used to
	// execute the code
	componentDidMount() {
		fetch("https://api.spacexdata.com/v2/launches")
			.then((res) => res.json())
			.then((json) => {
				this.setState({
					items: json,
					DataisLoaded: true
				});
			})
	}
	render() {
		const { DataisLoaded, items } = this.state;
		if (!DataisLoaded) return <div>
			<h1> Pleses wait some time.... </h1> </div> ;

		return (
		<div className = "App">
			<h1> Fetch data from an api in react </h1> {
				items.map((item) => (
                    
				<div key = { item.id } >
					
                <li className='user inline-flex m-[15px]' key={item.flight_number}>
                  <div className='rounded-md h-[500px] w-[500px] flex flex-col space-y-12 hover:bg-blue-200 transition duration-500 hover:scale-105'
                  style={{  
                    backgroundImage: 'url("' + item.links.mission_patch  + '")',
                    backgroundPosition: 'center',
                    backgroundSize: 'cover',
                    backgroundRepeat: 'no-repeat'
                  }}
                  >

                      
                      <div className="content-center text-3xl bg-yellow-200 rounded-t-md">{item.mission_name}</div>
                      <div className="bg-yellow-200 rounded-3xl content-center">
                        <div>
                        Rocket Name: {item.rocket.rocket_name} 
                        
                        </div>
                        <div>
                        Launch Year: {item.launch_year}
                        </div>
                        
                      </div>


                      <div className=" content-center bg-yellow-200">
                        {item.details}
                      </div>     
                      <a href={item.links.video_link} className="bg-red-400 text-red-50 bottom-0" target="blank">Click to see the Video :)</a>
 
                  </div>
                </li>



				</div>











			))
			}
		</div>
	);
}
}

export default App;
